sys_variable_value.value:
(function execute(inputs, outputs) {

    var inputArr = inputs.mrvsValues;
    var resultArr = [];
    var Approver = {};

    var i = 0;
    for (i=0; i<inputArr.length; i++) {
        Approver = inputArr[i];
        resultArr.push(Approver["ref_approver"]);

    }

    gs.info('Input: ' + JSON.stringify(inputArr) + ' Output: ' + resultArr.join());     

    outputs.apprarr = resultArr;   

})(inputs, outputs);

