sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...

// Get the GlideRecord of the RITM
var ritmSysID = inputs.ritm.sys_id;
var ritm = new GlideRecord('sc_req_item');
ritm.get(ritmSysID);  // Replace with your RITM sys_id if necessary

// Iterate through variables and log them
var variables = ritm.variables;
var output = {};

for (var key in variables) {
    if (variables.hasOwnProperty(key)) {
        output[key] = variables[key].getDisplayValue();
    }
}

// Return the variables
output;

})(inputs, outputs);

