sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...

    var actualOrg = inputs.parameter1;

    var apprGroupRecord = {
        'NL-01_N': 'Vor Ort Anwender-Support.NL.Nord.Approval',
        'NL-02_NW': 'Vor Ort Anwender-Support.NL.Nordwest.Approval',
        'NL-03_NO': 'Vor Ort Anwender-Support.NL.Nordost.Approval',
        'NL-04_O': 'Vor Ort Anwender-Support.NL.Ost.Approval',
        'NL-05_W': 'Vor Ort Anwender-Support.NL.West.Approval',
        'NL-06_RL': 'Vor Ort Anwender-Support.NL.Rheinland.Approval',
        'NL-07_WF': 'Vor Ort Anwender-Support.NL.Westfalen.Approval',
        'NL-08_SW': 'Vor Ort Anwender-Support.NL.Suedwest.Approval',
        'NL-09_NB': 'Vor Ort Anwender-Support.NL.Bayern-Nord.Approval',
        'NL-10_SB': 'Vor Ort Anwender-Support.NL.Bayern-Sued.Approval',
        'Zentrale': 'Vor Ort Anwender-Support.NL.Zentrale.Approval'
    };

    var actualGroup = apprGroupRecord[actualOrg];

    var numMembers = AutobahnCatalogUtils.groupHasMembers(actualGroup);
    if (numMembers < 1) {
        actualGroup = 'IT-Prozessmanagement.RequestFulfilment.Ops';
    }

    gs.info('OrgName = ' + actualGroup);

    outputs.approvergroup = actualGroup;

})(inputs, outputs);

